void DessinerGraphique(uint32_t* signal){
    
  
    int16_t signalAffiche[200]={};
    uint32_t max=0;
    uint32_t min = 1000000;
    for (int i=600; i <1001; i++) {
        if(signal[i] < min) {
            min=signal[i];
        }
        if(signal[i] > max) {
            max=signal[i];
        }
    }
    int32_t facteurEchelle = (max-min)/116;
    
    for (int i=600; i<1001 ; i=i+2) {
        signalAffiche[(i-600)/2] = ((signal[i]-min)/facteurEchelle);
        
    }
    GUI_DrawGraph(&signalAffiche[0], 200, 30, 20);
    
}
