void deplacerCurseur(void) {
  
    if (currentBouton0 != bouton0) {
        currentBouton0 = bouton0;
        currentOption ++;
        menuUpdate =true;
    }
    if (currentBouton1 != bouton1) {
        currentBouton1 = bouton1;
        currentOption--;
        menuUpdate =true;   
    }
    
    options = currentOption % NBOPTIONS  ;
    hauteurCercle = (16 + options*20) ;
    
 }

void changerValeur (void) {
    
    if (copieBouton0 != bouton0 && currentBouton0 !=bouton0) {
        currentBouton0 = bouton0;
        if (rcMinActif == true){
            rcMin--;
            afficherRCMin();
        }
         if (rcMaxActif == true){
            rcMax--;
            afficherRCMax();
        }
        if (SpO2MinActif ==true) {
            SPo2Min --;
            afficherSpO2Min();
        }
        if (DELirActif ==true) {
            if (courantDELi <= 4){
                courantDELi =0;
                afficherDELir();
            }
            else {
            courantDELi = courantDELi-5;
            afficherDELir();
            }
        }
        if (DELrActif ==true) {
            if (courantDELr <= 4){
                courantDELr =0;
                afficherDELr();
            }
            else {
            courantDELr = courantDELr-5;
            afficherDELr();
            }
        }
    
    }
     if (copieBouton1 != bouton1 && currentBouton1 != bouton1) {
        currentBouton1 = bouton1;
        if (rcMinActif == true){
            rcMin++;
            afficherRCMin();
        }
        if (rcMaxActif == true){
            rcMax++;
            afficherRCMax();
        }
        if (SpO2MinActif ==true) {
            SPo2Min ++;
            afficherSpO2Min();
        }
        if (DELirActif ==true) {
            if (courantDELi >= 46){
                courantDELi =50;
                afficherDELir();
            }
            else {
            courantDELi = courantDELi+5;
            afficherDELir();
            }
        }
         if (DELrActif ==true) {
            if (courantDELr >= 46){
                courantDELr =50;
                afficherDELr();
            }
            else {
            courantDELr = courantDELr+5;
            afficherDELr();
            }
        }
    }
    
}

void choixCurseur(void) {
    if (rcMinActif == false && rcMaxActif == false && SpO2MinActif == false && DELirActif ==false && DELrActif==false) {
        deplacerCurseur();
    }
    else {
        changerValeur();
    }
    
}

